mapper.delete:
delete
from lmp.collection
where user_id = #{userId,jdbcType=BIGINT}
and id = #{id,jdbcType=BIGINT}

mapper.update:
update lmp.collection
set user_id       = #{userId,jdbcType=BIGINT},
    session_id    = #{sessionId,jdbcType=BIGINT},
    session_title = #{sessionTitle,jdbcType=VARCHAR},
    created_at    = #{createdAt,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

mapper.delete:
delete
from lmp.collection
where user_id = #{userId}
  and session_id = #{sessionId}

mapper.select:
select id, user_id, session_id, session_title, created_at
from lmp.collection
where user_id = #{userId}
  and session_id = #{sessionId}
