mapper.update:
update file_content
set file_id    = #{fileId,jdbcType=BIGINT},
    type       = #{type,jdbcType=VARCHAR},
    content    = #{content,jdbcType=VARCHAR},
    parse_time = #{parseTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, file_id, type, content, parse_time
from file_content
where id = #{id,jdbcType=BIGINT}
