mapper.insert:

    SELECT currval(pg_get_serial_sequence('lmp.file_content', 'id')) AS id;

insert into lmp.file_content (file_id, type, content,
created_at, is_deleted, updated_at
)
values (#{fileId,jdbcType=BIGINT}, #{type,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR},
#{createdAt,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=SMALLINT},
#{updatedAt,jdbcType=TIMESTAMP}
)

mapper.select:
select id, file_id, type, content, created_at, is_deleted, updated_at
from file_content
where id = #{id,jdbcType=BIGINT}
