mapper.delete:
delete
from lmp.file
where id = #{id,jdbcType=BIGINT}
and user_id = #{userId,jdbcType=BIGINT}
and session_id = #{sessionId,jdbcType=BIGINT}

mapper.select:
select * from lmp.file

    and user_id = #{userId}
    and anonym_id = #{anonymId}
    and session_id = #{sessionId}
    and folder_id = #{folderId}
    and type = #{fileType}
    and storage_type = #{storageType}
    and upload_at >= #{uploadTimeStart}
    and upload_at <= #{uploadTimeEnd}
    and upload_status = #{uploadStatus}

order by ${orderBy} ${orderDir}
limit #{offset}, #{pageSize}

select count(*) from lmp.file


        and user_id = #{userId}


        and anonym_id = #{anonymId}


        and session_id = #{sessionId}


        and folder_id = #{folderId}


        and type = #{fileType}


        and storage_type = #{storageType}


         and upload_at >= #{uploadTimeStart} 


         and upload_at <= #{uploadTimeEnd} 


        and upload_status = #{uploadStatus}

select * from lmp.file
where upload_status = 0
 and upload_at < #{expireTime}

SELECT * FROM "lmp"."file"
WHERE 1=1

    AND "anonym_id" = #{anonymId}



        AND "user_id" = #{userId}

            AND "session_id" = #{sessionId}



AND "file_md5" = #{fileMd5}
LIMIT 1
