mapper.delete:
delete
from user_collection
where user_id = #{userId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

mapper.update:
update user_collection
set user_id         = #{userId,jdbcType=BIGINT},
    session_id      = #{sessionId,jdbcType=BIGINT},
    session_title   = #{sessionTitle,jdbcType=VARCHAR},
    collection_note = #{collectionNote,jdbcType=VARCHAR},
    collected_at    = #{collectedAt,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

mapper.delete:
delete
from user_collection
where user_id = #{param1}
  and session_id = #{param2}

mapper.select:
select id, user_id, session_id, session_title, collection_note, collected_at
from user_collection
where user_id = #{param1}
    session_id = #{param2}
